INSERT INTO [Game] ([Title],[Description],[YearPublished],[AverageRating],[CoverPicture]) VALUES ('Pokémon Unite','Pokémon Unite is a multiplayer online battle arena game, with standard matches consisting of two teams, each with 5 players. Each match is limited to 10 minutes in duration, and the team with the highest total score by the end of each match wins.', 2021, 7.3, 'https://archives.bulbagarden.net/media/upload/thumb/0/0d/Pok%C3%A9mon_UNITE_logo.png/250px-Pok%C3%A9mon_UNITE_logo.png');
INSERT INTO [Game] ([Title],[Description],[YearPublished],[AverageRating],[CoverPicture]) VALUES ('Pokémon Brilliant Diamond','Pokémon Brilliant Diamond and Pokémon Shining Pearl are 2021 remakes of the 2006 Nintendo DS role-playing video games Pokémon Diamond and Pearl.', 2021, 7.4, 'https://archives.bulbagarden.net/media/upload/thumb/3/33/Brilliant_Diamond_EN_boxart.png/250px-Brilliant_Diamond_EN_boxart.png');
