INSERT INTO [Game] ([Title],[Description],[YearPublished],[AverageRating],[CoverPicture]) VALUES ('Pokémon Legends: Arceus','The game begins with the player speaking with a disembodied voice, identified as Arceus, the creator deity of the Pokémon-Universe, which tasks the player with encountering all Pokémon in the Hisui region before transporting the player there through a rift in spacetime.', 2022, 7.6, 'https://upload.wikimedia.org/wikipedia/en/thumb/9/9c/Pokemon_Legends_Arceus_cover.jpg/220px-Pokemon_Legends_Arceus_cover.jpg');
INSERT INTO [Game] ([Title],[Description],[YearPublished],[AverageRating],[CoverPicture]) VALUES ('Pokémon Scarlet','The player character, having just moved to the Paldea region with their mother, now lives in the small settlement of Cabo Poco and prepares to start their studies at the Naranja AcademyS/Uva AcademyV in the region''s biggest city, Mesagoza.', 2022, 7.7, 'https://archives.bulbagarden.net/media/upload/thumb/5/5f/Scarlet_EN_boxart.png/250px-Scarlet_EN_boxart.png');
